import { Button, Input, Spinner, Textarea } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axiosInstance from "../../../../axios";
import { Checkbox, CheckboxGroup } from "@chakra-ui/react";
import { isAuthenticated } from "../../../../auth";
import { useToast } from "@chakra-ui/react";

function ProgressDetail(props) {
  const slug = props.match.params.slug;
  const [unplannedAction, setUnplannedAction] = useState("");
  const [learningFromConversation, setLearningFromConversation] = useState("");
  const [coachFeedback, setCoachFeedback] = useState("");
  const [mentorFeedback, setMentorFeedback] = useState("");
  const toast = useToast();

  const [progressDetails, setProgressDetails] = useState({
    loading: true,
    progress: null,
  });
  const [goals, setGoals] = useState([]);
  var months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const newDate = new Date();
  const currentMonth = months[newDate.getMonth()];
  const currentYear = newDate.getFullYear();

  useEffect(async () => {
    await axiosInstance.get(`founder/progress/details/${slug}`).then((res) => {
      const progressDetails = res.data;
      console.log("nilesp", progressDetails);
      setProgressDetails({ loading: false, progress: progressDetails });
      setLearningFromConversation(progressDetails.learning_conversation);
      setUnplannedAction(progressDetails.unplanned_action_help);
      setCoachFeedback(progressDetails.coach_feedback);
      setMentorFeedback(progressDetails.mentor_feedback);
    });
  }, [setProgressDetails]);

  const handleUpdate = (e) => {
    try {
      setProgressDetails({ ...progressDetails, loading: true });
      axiosInstance
        .put(`founder/progress/edit/${progressDetails.progress.id}/`, {
          founder_id: isAuthenticated().user_id,
          learning_conversation: learningFromConversation,
          unplanned_action_help: unplannedAction,
          mentor_feedback: mentorFeedback,
          coach_feedback: coachFeedback,
        })
        .then((res) => {
          setProgressDetails({ ...progressDetails, loading: false });
          toast({
            position: "top-right",
            title: "Successfully Updated.",
            description: "We've have updated the details for you.",
            status: "info",
            duration: 9000,
            isClosable: true,
          });
        });
    } catch (error) {
      console.log(error);
    }
  };

  return !progressDetails.loading ? (
    <div className="profile__header">
      <div>
        <h1>Weekely Updates</h1>
        <h1>{`${currentMonth} ${
          progressDetails.progress.start_date.split("-")[2]
        } - ${currentMonth} ${
          progressDetails.progress.end_date.split("-")[2]
        }, ${currentYear}`}</h1>
      </div>

      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1>Conversations</h1>
        <hr />

        <h1>Stakeholder you spoke to last week</h1>
        <Input />

        <h2>What did you learn from them</h2>
        <Textarea
          onChange={(e) => setCoachFeedback(e.currentTarget.value)}
          value={coachFeedback}
          height={200}
        />
      </div>

      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "20px", fontWeight: "600" }}>Mentor Feedback</h1>
        <Textarea
          onChange={(e) => setMentorFeedback(e.currentTarget.value)}
          value={mentorFeedback}
          height={200}
        />
        <hr style={{ margin: "10px 0 10px 0" }} />
        <h2 style={{ fontSize: "20px", fontWeight: "600" }}>Coach Feedback</h2>
        <Textarea
          onChange={(e) => setCoachFeedback(e.currentTarget.value)}
          value={coachFeedback}
          height={200}
        />
      </div>

      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "22px", fontWeight: "600" }}>
          Goals and Morals
        </h1>
        <div style={{ display: "flex" }}>
          <Input placeholder="Add Goals" />
          <Button>Add Goal</Button>
        </div>
      </div>
      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "20px", fontWeight: "600" }}>
          Unplanned Action that helped
        </h1>
        <Textarea
          onChange={(e) => setUnplannedAction(e.currentTarget.value)}
          value={unplannedAction}
          height={150}
        />
        <hr style={{ margin: "10px 0 10px 0" }} />
        <h2 style={{ fontSize: "20px", fontWeight: "600" }}>
          Learning From Conversation
        </h2>
        <Textarea
          onChange={(e) => setLearningFromConversation(e.currentTarget.value)}
          value={learningFromConversation}
          height={150}
        />
        <hr style={{ margin: "10px 0 10px 0" }} />
        <h2 style={{ fontSize: "20px", fontWeight: "600" }}>
          Planned Action that helped
        </h2>
        <Textarea
          value={progressDetails.progress.coach_feedback}
          height={150}
        />
      </div>
      <button
        onClick={handleUpdate}
        style={{
          backgroundColor: "#3aaeea",
          padding: "10px",
          borderRadius: "10px",
          marginTop: "30px",
          width: "20%",
        }}
      >
        Update
      </button>
    </div>
  ) : (
    <div
      style={{
        display: "flex",
        width: "60%",
        margin: "30px auto",
        height: "80vh",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Spinner
        thickness="4px"
        speed="0.65s"
        emptyColor="gray.200"
        color="blue.500"
        size="xl"
      />
    </div>
  );
}

export default ProgressDetail;
