import { Button, Input, Spinner, Textarea } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axiosInstance from "../../../../axios";
import { Checkbox, CheckboxGroup } from "@chakra-ui/react";

function ProgressDetail(props) {
  const slug = props.match.params.slug;
  const [unplannedAction,setUnplannedAction] = useState("")
  console.log("sfdfs",unplannedAction)
  const [progressDetails, setProgressDetails] = useState({
    loading: true,
    progress: null,
  });
  const [goals, setGoals] = useState([])
  var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];


  const newDate = new Date();
  const currentMonth = months[newDate.getMonth()]
  const currentYear = newDate.getFullYear()

  useEffect(async () => {
    await axiosInstance.get(`founder/progress/details/${slug}`).then((res) => {
      const progressDetails = res.data;
      console.log("nilesp", progressDetails);
      setProgressDetails({ loading: false, progress: progressDetails });
    });
  }, [setProgressDetails]);

  return !progressDetails.loading ? (
    <div className="profile__header">
      <div>
        <h1>Weekely Updates</h1>
        <h1>{`${currentMonth} ${progressDetails.progress.start_date.split("-")[2]} - ${currentMonth} ${progressDetails.progress.end_date.split("-")[2]}, ${currentYear}`}</h1>
      </div>
      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "20px", fontWeight: "600" }}>Mentor Feedback</h1>
        <Textarea value={progressDetails.progress.mentor_feedback} height={200}/>
        <hr style={{ margin: "10px 0 10px 0" }} />
        <h2 style={{ fontSize: "20px", fontWeight: "600" }}>Coach Feedback</h2>
        <Textarea value={progressDetails.progress.coach_feedback} height={200}/>
      </div>

      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "22px", fontWeight: "600" }}>
          Goals and Morals
        </h1>
        <div style={{ display: "flex" }}>
          <Input placeholder="Add Goals" />
          <Button>Add Goal</Button>
        </div>
      </div>
      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <FormControl id="unplanned_action" isRequired>
              <FormLabel mt={3}>Unplanned Action that helped</FormLabel>
              <Input
                value={progressDetails.progress.coach_feedback}
                placeholder="Last name"
              />
        </FormControl>
        <hr style={{ margin: "10px 0 10px 0" }} />
        <h2 style={{ fontSize: "20px", fontWeight: "600" }}>
          Learning From Conversation
        </h2>
        <Textarea value={progressDetails.progress.coach_feedback} height={150}/>
        <hr style={{ margin: "10px 0 10px 0" }} />
        <h2 style={{ fontSize: "20px", fontWeight: "600" }}>
          Planned Action that helped
        </h2>
        <Textarea value={progressDetails.progress.coach_feedback} height={150} />
      </div>
      <button style={{backgroundColor:'#3aaeea', padding:"10px", borderRadius:'10px', marginTop:'30px', width:'20%'}}>Update</button>
    </div>
  ) : (
    <div
      style={{
        display: "flex",
        width: "60%",
        margin: "30px auto",
        height: "80vh",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Spinner
        thickness="4px"
        speed="0.65s"
        emptyColor="gray.200"
        color="blue.500"
        size="xl"
      />
    </div>
  );
}

export default ProgressDetail;
