import { Button, Input, Spinner, Textarea } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axiosInstance from "../../../../axios";
import { Checkbox, CheckboxGroup } from "@chakra-ui/react";

function ProgressDetail(props) {
  const slug = props.match.params.slug;
  const [progressDetails, setProgressDetails] = useState({
    loading: true,
    progress: null,
  });

  useEffect(async () => {
    await axiosInstance.get(`founder/progress/details/${slug}`).then((res) => {
      const progressDetails = res.data;
      console.log("nilesp", progressDetails);
      setProgressDetails({ loading: false, progress: progressDetails });
    });
  }, [setProgressDetails]);

  return !progressDetails.loading ? (
    <div className="profile__header">
      <div>
        <h1>Weekely Updates</h1>
        <h1>Jan 11-Jan 17, 2021</h1>
      </div>
      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "20px", fontWeight: "600" }}>Mentor Feedback</h1>
        <Textarea placeholder={progressDetails.progress.mentor_feedback} />
        <hr style={{ margin: "10px 0 10px 0" }} />
        <h2 style={{ fontSize: "20px", fontWeight: "600" }}>Coach Feedback</h2>
        <Textarea placeholder={progressDetails.progress.coach_feedback} />
      </div>

      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "22px", fontWeight: "600" }}>
          Goals and Morals
        </h1>
        <div style={{ display: "flex" }}>
          <Input placeholder="Add Goals" />
          <Button>Add Goal</Button>
        </div>
      </div>
      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "25px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "20px", fontWeight: "600" }}>
          Unplanned Action that helped
        </h1>
        <Textarea
          placeholder={progressDetails.progress.unplanned_action_help}
        />
        <hr style={{ margin: "10px 0 10px 0" }} />
        <h2 style={{ fontSize: "20px", fontWeight: "600" }}>
          Learning From Conversation
        </h2>
        <Textarea placeholder={progressDetails.progress.coach_feedback} />
        <hr style={{ margin: "10px 0 10px 0" }} />
        <h2 style={{ fontSize: "20px", fontWeight: "600" }}>
          Planned Action that helped
        </h2>
        <Textarea placeholder={progressDetails.progress.coach_feedback} />
      </div>
      <Button>Update</Button>
    </div>
  ) : (
    <div
      style={{
        display: "flex",
        width: "60%",
        margin: "30px auto",
        height: "80vh",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Spinner
        thickness="4px"
        speed="0.65s"
        emptyColor="gray.200"
        color="blue.500"
        size="xl"
      />
    </div>
  );
}

export default ProgressDetail;
