import React, { useState } from "react";
import {
  Box,
  Flex,
  IconButton,
  useColorMode,
  Heading,
  Text,
  Link,
  FormControl,
  FormLabel,
  Input,
  Stack,
  Button,
  Image,
  HStack,
} from "@chakra-ui/react";
import { Redirect } from "react-router-dom";
import axiosInstance from "../../axios";
import { useHistory } from "react-router-dom";
import { isAuthenticated } from "../../auth";
import Header from "./NavBar/header/Header";
import { useToast } from "@chakra-ui/react";

const SignUp = () => {
  return (
    <div>
      <Flex
        minHeight="100vh"
        width="full"
        align="center"
        justifyContent="center"
      >
        <Box
          borderWidth={1}
          px={10}
          py={5}
          width="full"
          maxWidth="900px"
          textAlign="center"
          boxShadow="xl"
        >
          <Flex justifyContent="space-around" alignItems="center">
            <Box p={4}>
              <img src="https://global-uploads.webflow.com/5f3c09d8572b1a3453aa9c1f/5faac65b405f8d172ef75b8e_Sparklehood%20logo%20grey%20login%20page-p-500.png" />
              <LoginHeader />
              <LoginForm />
            </Box>
            <div className="login__image">
              <Box>
                <img src="https://global-uploads.webflow.com/5f3c09d8572b1a3453aa9c1f/5faab7a66dfdec1ed6d2ecca_Sparklehood%20platform%20login%20page-p-500.png" />
              </Box>
            </div>
          </Flex>
        </Box>
      </Flex>
    </div>
  );
};

const ThemeSelector = () => {
  const { colorMode, toggleColorMode } = useColorMode();
  return (
    <Box textAlign="right">
      <IconButton onClick={toggleColorMode} />
    </Box>
  );
};

const LoginHeader = () => {
  return (
    <div>
      <Stack
        fontSize="15px"
        isInline
        justifyContent="space-between"
        mt={4}
      ></Stack>
      <Box textAlign="center">
        <Heading fontSize="35px" pt={3}>
          Sign Up
        </Heading>
      </Box>
    </div>
  );
};

const LoginForm = () => {
  const history = useHistory();
  const toast = useToast();

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(email, password, name);

    axiosInstance
      .post(`user/create/`, {
        email: email,
        user_name: name,
        password: password,
      })
      .then((res) => {
        toast({
          position: "top-left",
          title: "Message",
          description: "We have send you the verification email",
          status: "info",
          duration: 5000,
          isClosable: true,
        });
        history.push("/login");
        console.log(res);
        console.log(res.data);
      });
  };

  return (
    <Box my={8} textAlign="center">
      <form>
        <FormControl>
          <FormLabel>Full Name</FormLabel>
          <Input
            type="text"
            placeholder=""
            required
            onChange={(e) => setName(e.currentTarget.value)}
          />
        </FormControl>
        <FormControl>
          <FormLabel>Email</FormLabel>
          <Input
            type="email"
            placeholder=""
            required
            onChange={(e) => setEmail(e.currentTarget.value)}
          />
        </FormControl>
        <div className="form__password">
          <div>
            <FormControl mt={4}>
              <FormLabel>Password</FormLabel>
              <Input
                type="password"
                placeholder=""
                required
                onChange={(e) => setPassword(e.currentTarget.value)}
              />
            </FormControl>
          </div>
          <div>
            <FormControl mt={4}>
              <FormLabel>Confirm Password</FormLabel>
              <Input
                type="password"
                required
                placeholder=""
                onChange={(e) => setPassword(e.currentTarget.value)}
              />
            </FormControl>
          </div>
        </div>

        <Button colorScheme="blue" width="full" mt={4} onClick={handleSubmit}>
          Sign Up
        </Button>
        <br />

        <Button
          colorScheme="white"
          mt={2}
          color="black"
          width="full"
          boxShadow="lg"
        >
          Continue With Google
        </Button>
      </form>
    </Box>
  );
};

export default SignUp;
