import { Button, Spinner, Stack } from "@chakra-ui/react";
import { Checkbox, CheckboxGroup } from "@chakra-ui/react";
import axios from "axios";

import React, { useEffect, useState } from "react";
import { isAuthenticated } from "../../../../auth";
import axiosInstance from "../../../../axios";

function Progress(props) {
  const [isLoading, setIsLoading] = useState(false);
  const [date, setDate] = useState();
  const [startDate, setStartDate] = useState();
  const [endDate, setEndDate] = useState();
  const [progress, setProgress] = useState({ loading: "false", data: null });
  const [updated, setUpdated] = useState(false);
  var months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const newDate = new Date();
  const currentNewDate = parseInt(newDate.getDate());
  const currentMonth = months[newDate.getMonth()];
  const currentYear = newDate.getFullYear();

  console.log(currentMonth);
  useEffect(async () => {
    try {
      setIsLoading(true);
      await axiosInstance
        .get(`founder/progress/${isAuthenticated().user_id}/`)
        .then((res) => {
          setIsLoading(false);
          const allProgress = res.data;
          console.log("all", allProgress);
          setProgress({ load: "true", data: allProgress });
          const date = res.data[0].end_date;
          const resp = date.split("-");
          setDate(resp[2]);
        });
    } catch (error) {
      console.log(error);
    }
  }, [setProgress, updated]);

  const handleSubmit = async (e) => {
    setIsLoading(true);
    try {
      await axiosInstance
        .post(`founder/add/`, {
          founder_id: isAuthenticated().user_id,
          slug: parseInt(Math.random() * 1000000),
        })
        .then((res) => {
          setIsLoading(false);
          setUpdated(!updated);
        });
    } catch (error) {
      console.log(error);
    }
  };

  return progress.loading || isLoading ? (
    <div
      style={{
        display: "flex",
        width: "60%",
        margin: "30px auto",
        height: "80vh",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Spinner
        thickness="4px"
        speed="0.65s"
        emptyColor="gray.200"
        color="blue.500"
        size="xl"
      />
    </div>
  ) : (
    <div className="progress__maintab">
      <div
        style={{
          padding: "30px 50px 20px 0",
          display: "flex",
          justifyContent: "space-around",
          textAlign: "center",
          alignItems: "center",
        }}
      >
        <h1 style={{ fontSize: "35px" }}>Weekely Update</h1>
        <div className={date >= currentNewDate ? "disable" : "enable"}>
          <button className="button_progress" onClick={handleSubmit}>
            Add Progress
          </button>
        </div>
      </div>
      <div
        style={{
          backgroundColor: "white",
          display: "flex",
          justifyContent: "space-around",
        }}
      >
        <div style={{ backgroundColor: "white", width: "50%" }}>
          <div>
            {progress.data.map((res) => {
              return (
                <div className="progress__main">
                  <div className="progress__date">
                    <h1>{`${currentMonth} ${
                      res.start_date.split("-")[2]
                    } - ${currentMonth} ${
                      res.end_date.split("-")[2]
                    }, ${currentYear}`}</h1>
                    <a href={`details/${res.slug}`} _blank="true">
                      Edit
                    </a>
                  </div>
                  <div className="progress__data">
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-around",
                        textAlign: "center",
                        paddingBottom: "10px",
                      }}
                    >
                      <h1>Primary Metric</h1>
                      <h1>Conversation</h1>
                    </div>
                    <hr />
                    <div
                      style={{
                        display: "flex",
                        justifyContent: "space-around",
                        textAlign: "center",
                        paddingTop: "10px",
                      }}
                    >
                      <h1>{res.primary_metric}</h1>
                      <h1>{res.conversation}</h1>
                    </div>
                  </div>
                  <div className="progress__feedback">
                    <h1 style={{ fontSize: "20px", fontWeight: "700" }}>
                      Mentor Feedback
                    </h1>
                    <p style={{ fontSize: "10px" }}>{res.mentor_feedback}</p>
                  </div>
                  <div className="progress__goals">
                    <h1>{`Goals (0/${res.goals.length})`}</h1>
                    <Stack pl={6} mt={1} spacing={1}>
                      {res.goals.map((goal) => {
                        return <Checkbox>{goal}</Checkbox>;
                      })}
                    </Stack>
                  </div>
                </div>
              );
            })}
          </div>
        </div>
        <div style={{ marginTop: "40px" }}>
          <div
            style={{
              width: "450px",
              height: "300px",
              border: "1px solid black",
              textAlign: "center",
              borderRadius: "10px",
            }}
          >
            <h1>Graph 1</h1>
          </div>
          <div
            style={{
              width: "450px",
              height: "300px",
              border: "1px solid black",
              marginTop: "30px",
              textAlign: "center",
              borderRadius: "10px",
            }}
          >
            <h1>Graph 2</h1>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Progress;
