import { Button, Input, Spinner, Textarea } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axiosInstance from "../../../../axios";
import { Checkbox, CheckboxGroup } from "@chakra-ui/react";
import { isAuthenticated } from "../../../../auth";
import { useToast } from "@chakra-ui/react";
import { List, ListItem, ListIcon } from "@chakra-ui/react";
import { PhoneIcon, CloseIcon, WarningIcon } from "@chakra-ui/icons";

function ProgressDetail(props) {
  const slug = props.match.params.slug;
  const [unplannedAction, setUnplannedAction] = useState("");
  const [learningFromConversation, setLearningFromConversation] = useState("");
  const [coachFeedback, setCoachFeedback] = useState("");
  const [mentorFeedback, setMentorFeedback] = useState("");
  const toast = useToast();

  const [progressDetails, setProgressDetails] = useState({
    loading: true,
    progress: null,
  });
  const [tempGoals, setTempGoals] = useState("");
  const [goals, setGoals] = useState([]);

  const [tempAction, setTempAction] = useState("");
  const [action, setAction] = useState([]);

  var months = [
    "January",
    "February",
    "March",
    "April",
    "May",
    "June",
    "July",
    "August",
    "September",
    "October",
    "November",
    "December",
  ];

  const newDate = new Date();
  const currentMonth = months[newDate.getMonth()];
  const currentYear = newDate.getFullYear();

  useEffect(async () => {
    await axiosInstance.get(`founder/progress/details/${slug}`).then((res) => {
      const progressDetails = res.data;
      console.log("nilesp", progressDetails);
      setProgressDetails({ loading: false, progress: progressDetails });
      setLearningFromConversation(progressDetails.learning_conversation);
      setUnplannedAction(progressDetails.unplanned_action_help);
      setGoals(progressDetails.goals);
      setAction(progressDetails.action_helped);
    });
  }, [setProgressDetails]);

  const handleUpdate = (e) => {
    try {
      setProgressDetails({ ...progressDetails, loading: true });
      axiosInstance
        .put(`founder/progress/edit/${progressDetails.progress.id}/`, {
          founder_id: isAuthenticated().user_id,
          goals: goals,
          action_helped: action,
        })
        .then((res) => {
          setProgressDetails({ ...progressDetails, loading: false });
          toast({
            position: "top-right",
            title: "Successfully Updated.",
            description: "We've have updated the details for you.",
            status: "info",
            duration: 9000,
            isClosable: true,
          });
        });
    } catch (error) {
      console.log(error);
    }
  };

  const handleGoalSubmit = () => {
    if (goals) {
      setGoals([...goals, tempGoals]);
    } else {
      setGoals([tempGoals]);
    }
    setTempGoals("");
  };

  const handleActionSubmit = () => {
    if (action) {
      setAction([...action, tempAction]);
    } else {
      setAction([tempAction]);
    }
    setTempAction("");
  };

  return !progressDetails.loading ? (
    <div className="profile__header">
      <div>
        <h1 style={{ fontSize: "45px" }}>Weekely Updates</h1>
        <h1 style={{ fontSize: "30px" }}>{`${currentMonth} ${
          progressDetails.progress.start_date.split("-")[2]
        } - ${currentMonth} ${
          progressDetails.progress.end_date.split("-")[2]
        }, ${currentYear}`}</h1>
      </div>

      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "15px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "20px", fontWeight: "600" }}>Conversations</h1>
        <hr style={{ margin: "10px 0 10px 0" }} />

        <h1 style={{ fontSize: "18px" }}>Stakeholder you spoke to last week</h1>
        <Input
          fontSize="15px"
          backgroundColor="whitesmoke"
          mt={2}
          mb={2}
          height={6}
        />

        <h1 style={{ fontSize: "18px" }}>What did you learn from them</h1>
        <Textarea
          mt={2}
          mb={2}
          fontSize="15px"
          backgroundColor="whitesmoke"
          onChange={(e) => setCoachFeedback(e.currentTarget.value)}
          value={coachFeedback}
          height={150}
        />
      </div>

      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "15px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "20px", fontWeight: "600" }}>Primary Metric</h1>
        <hr style={{ margin: "10px 0 10px 0" }} />

        <h1 style={{ fontSize: "18px" }}>Choose Your Primary Metric</h1>
        <Input
          fontSize="15px"
          backgroundColor="whitesmoke"
          mt={2}
          mb={2}
          height={6}
        />

        <h1 style={{ fontSize: "18px" }}>
          Primary Metric for last week in INR
        </h1>
        <Input
          fontSize="15px"
          backgroundColor="whitesmoke"
          mt={2}
          mb={2}
          height={6}
        />

        <h1 style={{ fontSize: "18px" }}>What is target Market</h1>
        <Input
          fontSize="15px"
          backgroundColor="whitesmoke"
          mt={2}
          mb={2}
          height={6}
        />

        <h1 style={{ fontSize: "18px" }}>
          Unplanned actions that helped you achieve your last week goals?
        </h1>
        <Textarea
          fontSize="15px"
          mt={2}
          mb={2}
          backgroundColor="whitesmoke"
          onChange={(e) => setCoachFeedback(e.currentTarget.value)}
          value={coachFeedback}
          fontSize="15px"
          height={150}
        />
        <h1 style={{ fontSize: "18px" }}>
          Actions that didn’t have any positive impact on your startup?
        </h1>
        <Textarea
          fontSize="15px"
          mt={2}
          mb={2}
          fontSize="15px"
          backgroundColor="whitesmoke"
          onChange={(e) => setCoachFeedback(e.currentTarget.value)}
          value={coachFeedback}
          height={150}
        />
      </div>

      <div
        style={{
          border: "1px solid gainsboro",
          borderRadius: "10px",
          padding: "15px",
          marginTop: "40px",
          backgroundColor: "white",
        }}
      >
        <h1 style={{ fontSize: "20px", fontWeight: "600" }}>
          Think Out Loud!!
        </h1>
        <hr style={{ margin: "10px 0 10px 0" }} />

        <h1 style={{ fontSize: "18px" }}>
          Reflect about where you startup stands right now and list your top
          priorities.
        </h1>
        <Textarea
          fontSize="15px"
          backgroundColor="whitesmoke"
          mt={2}
          mb={2}
          height={150}
        />

        <h1 style={{ fontSize: "18px" }}>
          Based on the priorities listed above, decide specific and measurable
          goals for next week.
        </h1>
        <div
          style={{
            display: "flex",
            justifyContent: "space-around",
            alignItems: "center",
          }}
        >
          <Input
            fontSize="15px"
            value={tempGoals}
            backgroundColor="whitesmoke"
            onChange={(e) => setTempGoals(e.currentTarget.value)}
            mt={2}
            mb={2}
            height={10}
            width="80%"
          />
          <Button onClick={handleGoalSubmit} backgroundColor="#3aaeea">
            Add Goals
          </Button>
        </div>
        <div>
          {goals
            ? goals.map((goal) => {
                return (
                  <List ml={3} spacing={3}>
                    <div
                      style={{
                        display: "flex",
                        alignItems: "center",
                        width: "30%",
                      }}
                    >
                      <ListItem mr={5}>{goal}</ListItem>
                      <CloseIcon />
                    </div>
                  </List>
                );
              })
            : ""}
        </div>
        <h1 style={{ fontSize: "18px", marginTop: "20px" }}>
          What actions will you take to achieve your goals?
        </h1>
        <div
          style={{
            display: "flex",
            justifyContent: "space-around",
            alignItems: "center",
          }}
        >
          <Input
            fontSize="15px"
            value={tempAction}
            backgroundColor="whitesmoke"
            onChange={(e) => setTempAction(e.currentTarget.value)}
            mt={2}
            mb={2}
            height={10}
            width="80%"
          />
          <Button onClick={handleActionSubmit} backgroundColor="#3aaeea">
            Add Action
          </Button>
        </div>
        <div>
          {action
            ? action.map((goal) => {
                return (
                  <List ml={3} spacing={3}>
                    <div
                      style={{
                        display: "flex",
                        alignItems: "center",
                        width: "30%",
                      }}
                    >
                      <ListItem mr={5}>{goal}</ListItem>
                      <CloseIcon />
                    </div>
                  </List>
                );
              })
            : ""}
        </div>
      </div>

      <button
        onClick={handleUpdate}
        style={{
          backgroundColor: "#3aaeea",
          padding: "10px",
          borderRadius: "10px",
          margin: "30px",
          width: "20%",
        }}
      >
        Update
      </button>
    </div>
  ) : (
    <div
      style={{
        display: "flex",
        width: "60%",
        margin: "30px auto",
        height: "80vh",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Spinner
        thickness="4px"
        speed="0.65s"
        emptyColor="gray.200"
        color="blue.500"
        size="xl"
      />
    </div>
  );
}

export default ProgressDetail;
