import { Button, Spinner, Stack } from "@chakra-ui/react";
import { Checkbox, CheckboxGroup } from "@chakra-ui/react";
import axios from "axios";

import React, { useEffect, useState } from "react";
import { isAuthenticated } from "../../../../auth";
import axiosInstance from "../../../../axios";

function Progress(props) {
  const [isLoading, setIsLoading] = useState(false);
  const [date, setDate] = useState();
  const [startDate, setStartDate] = useState()
  const [endDate, setEndDate] = useState()
  const [progress, setProgress] = useState({ loading: "false", data: null });
  const [updated, setUpdated] = useState(false);
  var months = ["January", "February", "March", "April", "May", "June", "July", "August", "September", "October", "November", "December"];

  const newDate = new Date();
  const currentNewDate = parseInt(newDate.getDate());
  const currentMonth = months[newDate.getMonth()]

  console.log(currentMonth)
  useEffect(async () => {
    try {
      setIsLoading(true);
      await axiosInstance
        .get(`founder/progress/${isAuthenticated().user_id}/`)
        .then((res) => {
          setIsLoading(false);
          const allProgress = res.data;
          console.log("all", allProgress);
          setProgress({ load: "true", data: allProgress });
          const date = res.data[0].end_date;
          const resp = date.split("-");
          setDate(resp[2]);
        });
    } catch (error) {
      console.log(error)
    }

  }, [setProgress, updated]);

  const handleSubmit = async (e) => {
    setIsLoading(true);
    try {
      await axiosInstance
        .post(`founder/add/`, {
          founder_id: isAuthenticated().user_id,
          slug: parseInt(Math.random() * 1000000),
        })
        .then((res) => {
          setIsLoading(false);
          setUpdated(!updated);
        });
    } catch (error) {
      console.log(error);
    }
  };

  return progress.loading || isLoading ? (
    <div
      style={{
        display: "flex",
        width: "60%",
        margin: "30px auto",
        height: "80vh",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Spinner
        thickness="4px"
        speed="0.65s"
        emptyColor="gray.200"
        color="blue.500"
        size="xl"
      />
    </div>
  ) : (
    <div style={{ backgroundColor: "white" }}>
      <div
        style={{
          width: "70%",
          margin: "auto",
          paddingTop: "30px",
          display: "flex",
          justifyContent: "space-between",
          textAlign: "center",
        }}
      >
        <h1 style={{ fontSize: "35px" }}>Weekely Update</h1>
        <div className={date >= currentNewDate ? "disable" : "enable"}>
          <Button size="md" colorScheme="blue" onClick={handleSubmit}>
            Add Update
          </Button>
        </div>
      </div>
      <div>
        {progress.data.map((res) => {
          return (
            <div
              style={{
                width: "70%",
                margin: "20px auto",
                backgroundColor: "white",
              }}
            >
              <div className="progress__date">
                <h1>{`${res.start_date.split("-")[2]}-${res.end_date} , ${currentMonth}`}</h1>
                <a href={`details/${res.slug}`} _blank="true">
                  Edit
                </a>
              </div>
              <div className="progress__data">
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-around",
                    textAlign: "center",
                    paddingBottom: "10px",
                    fontWeight: "800",
                  }}
                >
                  <h1>WTL</h1>
                  <h1>Morale</h1>
                  <h1>Users talked to</h1>
                </div>
                <hr />
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-around",
                    textAlign: "center",
                    paddingTop: "10px",
                  }}
                >
                  <h1>WTL</h1>
                  <h1>Morale</h1>
                  <h1>Users talked to</h1>
                </div>
              </div>
              <div className="progress__goals">
                <h1>Goals (1/2)</h1>
                <Stack pl={6} mt={1} spacing={1}>
                  <Checkbox>Child Checkbox 1</Checkbox>
                  <Checkbox>Child Checkbox 2</Checkbox>
                </Stack>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}

export default Progress;
