import React, { useEffect, useState } from "react";
import { Select, Spinner } from "@chakra-ui/react";
import axiosInstance from "../../../../axios";
import Post from "./Post";
<Spinner
  thickness="4px"
  speed="0.65s"
  emptyColor="gray.200"
  color="blue.500"
  size="xl"
/>;

function Library() {
  const [post, setPost] = useState({ loading: true, post: null });
  const [category, setCategory] = useState({ refresh: true, option: null });
  console.log(post);

  useEffect(async () => {
    try {
      await axiosInstance.get(`company/category`).then((res) => {
        const allCategory = res.data;
        setCategory({ refresh: false, option: allCategory });
      });
    } catch (error) {
      console.log(error);
    }
  }, [setCategory]);

  useEffect(async () => {
    await axiosInstance.get(`company/post`).then((res) => {
      const allPost = res.data;
      setPost({ loading: false, post: allPost });
    });
  }, [setPost]);

  return !post.loading ? (
    <div style={{ width: "60%", margin: "30px auto", height: "100%" }}>
      <div>
        <Select placeholder="Select option" size="lg">
          <option>Customer</option>
          <option>Product</option>
          <option>MVP</option>
          <option>Startup</option>
        </Select>
      </div>
      <div className="all__post">
        <Post posts={post.post} />
      </div>
    </div>
  ) : (
    <div
      style={{
        display: "flex",
        width: "60%",
        margin: "30px auto",
        height: "80vh",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Spinner
        thickness="4px"
        speed="0.65s"
        emptyColor="gray.200"
        color="blue.500"
        size="xl"
      />
    </div>
  );
}

export default Library;
