import { Button, Spinner } from "@chakra-ui/react";
import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axiosInstance from "../../axios";
import "./style.css";

const VerifyEmail = (props) => {
  const [valid, setValid] = useState(false);
  const [isLoading, setIsLoading] = useState(false);

  const verification_token = props.match.params.token;

  useEffect(() => {
    try {
      setIsLoading(true);
      axiosInstance
        .get(`user/email-verify/`, {
          params: {
            token: verification_token,
          },
        })
        .then((res) => {
          console.log("res", res);
          setIsLoading(false);
          setValid(true);
        })
        .catch((err) => {
          setIsLoading(false);
          console.log("err", err);
        });
    } catch (error) {
      console.log(error);
    }
  }, []);

  return isLoading ? (
    <div
      style={{
        display: "flex",
        width: "60%",
        margin: "30px auto",
        height: "80vh",
        textAlign: "center",
        justifyContent: "center",
        alignItems: "center",
      }}
    >
      <Spinner
        thickness="4px"
        speed="0.65s"
        emptyColor="gray.200"
        color="blue.500"
        size="xl"
      />
    </div>
  ) : (
    <div className="verification__page">
      <h1
        style={{ fontSize: "60px", fontWeight: "bolder", fontFamily: "Mosk" }}
      >
        Welcome to Sparklehood
      </h1>
      {valid ? (
        <div>
          <h1>
            Verification Succesfull please click on the button below to login
          </h1>
          <Link to="/login">
            <Button colorScheme="blue" borderRadius={25} width={150}>
              Login
            </Button>
          </Link>
        </div>
      ) : (
        <div>
          <h1>The Link You are using is either expired or is not valid</h1>
          <Link to="/login">
            <Button mt={10} colorScheme="blue" borderRadius={25} width={200}>
              Resend Email
            </Button>
          </Link>
        </div>
      )}
    </div>
  );
};

export default VerifyEmail;
